itle <> "") { ?>                                                        <h1><a href="<?php echo esc_url($image_link_url); ?>"><?php echo cs_allow_special_char($title) ?></a></h1>                                                    <?php } ?>                                                     <p><?PHP echo esc_html($description); ?></p>                                                    <?PHP if ($imgLink == true) { ?>                                                        <a href="<?php echo esc_url($image_link_url); ?>" target="_blank">Read More</a>                                                    <?php } ?>                                                 </div>                                            </li>                                            <?php                                        }                                    }                                    ?>                                </ul>                            </div>                            <div id="carousel">                                <ul class="slides">                                    <?php                                    foreach ($cs_xmlObject->children() as $cs_node) {                                        if ($cs_node->getName() == "gallery") {                                            $counter_gal++;                                            $counter = $post->ID . $counter_gal;                                            //cs_gallery_singe_gallery();                                            global $cs_node, $counter;                                            if (isset($_POST['counter']))                                                $counter = $_POST['counter'];                                            if (isset($_POST['path']))                                                $cs_node->path = $_POST['path'];                                            ?>                                            <?php $image_path = wp_get_attachment_image_src((int) $cs_node->path, array($video_width_thumb, $video_height_thumb)); ?>                                            <li>                                                <img src="<?php echo esc_url($image_path[0]) ?>" alt="gallery tabs image">                                            </li>                                            <?php                                        }                                    }                                    ?>                                  </ul>                            </div>                        </div>                    </div>                </div>            </div>        </section>    <?php }else { ?>        <?php //cs_framework::cs_enqueue_prettyphoto_script(); ?>        <?php        // For pagination process        if (empty($_GET['page_id_all']))            $_GET['page_id_all'] = 1;        // pagination start        if ($cs_meta_gallery_options <> "") {            $cs_xmlObject = new SimpleXMLElement($cs_meta_gallery_options);            if ($paginationLimit > 0) {                $limit_start = $paginationLimit * ($_GET['page_id_all'] - 1);                $limit_end = $limit_start + $paginationLimit;                $count_post = count($cs_xmlObject->gallery);                if ($limit_end > count($cs_xmlObject->gallery))                    $limit_end = count($cs_xmlObject->gallery);            }            else {                $limit_start = 0;                $limit_end = count($cs_xmlObject->gallery);                $count_post = count($cs_xmlObject->gallery);            }        }        $width = '250';        $height = '188';        cs_framework::cs_enqueue_gallery_script();        cs_framework::cs_enqueue_prettyphoto_script();        ?>        <section style="margin:0 0 20px 0;" class="page-section">            <div class="container">                <div class="row">	                    <div class="col-md-12">                        <section